package Basics;

public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=23;
		int y=25;
		int temp;
		temp=x;
		x=y;
		y=temp;
		System.out.println(x + " " + y);
		
		
		
		int a=25;
		int b=23;
		a=a+b;
		b=a-b;
		a=a-b;
		System.out.println(a + " " + b);
		

	}

}
