package ControlStatements;

public class Ifelseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age=60;
		if(age<18) {
			System.out.println("Minor");
			
		}
		else if(age==18) {
			System.out.println("Eligible");
		}
		else {
			System.out.println("Retired");
		}
		

	}

}
