package ControlStatements;

public class Largest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		int b=30;
		int c=10;
		if(a>b) {
			
			if(a>c) {
				System.out.println(a);
			}else {
				System.out.println(c);
			}
		}
		else {
			System.out.println(b);
		}
	}

}
