package ControlStatements;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int marks=50;
		
		switch(marks) {
		case 1:
			
			System.out.println("Good");
			break;
		case 2:
			System.out.println("Average");
			break;
		case 3:
			System.out.println("Below Average");
			break;
		default:
		
			System.out.println("hi");
			break;
		}

	}

}
