package Loop;

public class Dowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		do 
		{
			System.out.println(i);
			i++;
		}while(i<=10);
		System.out.println("odd numbers");
		int n=1;
		do {
			System.out.println(n);
			n+=2;
		}while(n<=10);
	}

}
